import {resolve} from 'path';

import Koa from 'koa';
import * as R from 'ramda';

const MIDDLEWARES = ['router'];

const useMiddlewares = (app) => {
  R.map(
    R.compose(
      R.forEachObjIndexed(
        initWith => initWith(app)
      ),
      require,
      name => resolve(__dirname, `./middlewares/${name}`)
    )
  )(MIDDLEWARES)
}

// 启动逻辑代码
async function start() {
  const app = new Koa();
  await useMiddlewares();
  
  app.listen(3000);
}

start();

